.mdc-picture-background {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    overflow: hidden;
    z-index: 1
}

.mdc-picture-background picture {
    height: 100%;
    width: 100%
}

.mdc-picture-background picture img {
    object-fit: cover;
    object-position: 50% 50%;
    width: 100%;
    height: 100%
}

@media (min-width:840px) {
    .bg-none-desktop,
    .mdc-picture-background.mdc-picture-none-desktop {
        display: none
    }
}

@media (min-width:480px) and (max-width:839px) {
    .bg-none-tablet,
    .mdc-picture-background.mdc-picture-none-tablet {
        display: none
    }
}

@media (max-width:479px) {
    .bg-none-phone,
    .mdc-picture-background.mdc-picture-none-phone {
        display: none
    }
}

.bg-align-top {
    background-position-y: top
}

.bg-align-top picture img {
    object-fit: cover;
    object-position: 50% 0
}

.bg-align-top.bg-align-left picture img {
    object-position: 0 0
}

.bg-align-top.bg-align-right picture img {
    object-position: 100% 0
}

.bg-align-bottom {
    background-position-y: bottom
}

.bg-align-bottom picture img {
    object-fit: cover;
    object-position: 50% 100%
}

.bg-align-bottom.bg-align-left picture img {
    object-position: 0 100%
}

.bg-align-bottom.bg-align-right picture img {
    object-position: 100% 100%
}

.bg-size-contain {
    background-size: contain
}

.bg-size-contain picture img {
    object-fit: contain
}

