.mdc-menu-surface {
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12);
    background-color: #fff;
    background-color: var(--mdc-theme-surface, #fff);
    color: #000;
    color: var(--mdc-theme-on-surface, #000);
    transform-origin-left: top left;
    transform-origin-right: top right;
    border-radius: 4px;
    display: none;
    position: absolute;
    box-sizing: border-box;
    max-width: calc(100vw - 32px);
    max-height: calc(100vh - 32px);
    margin: 0;
    padding: 0;
    transform: scale(1);
    transform-origin: top left;
    opacity: 0;
    overflow: auto;
    will-change: transform, opacity;
    z-index: 4
}

.mdc-menu-surface[dir=rtl],
[dir=rtl] .mdc-menu-surface {
    transform-origin-left: top right;
    transform-origin-right: top left
}

.mdc-menu-surface:focus {
    outline: none
}

.mdc-menu-surface--animating-open {
    display: inline-block;
    transform: scale(.8);
    transition: opacity .03s linear, transform .12s cubic-bezier(0, 0, .2, 1);
    opacity: 0
}

.mdc-menu-surface--open {
    display: inline-block;
    transform: scale(1);
    opacity: 1
}

.mdc-menu-surface--animating-closed {
    display: inline-block;
    transition: opacity 75ms linear;
    opacity: 0
}

.mdc-menu-surface--anchor {
    position: relative;
    overflow: visible
}

.mdc-menu-surface--fixed {
    position: fixed
}

.mdc-menu {
    min-width: 112px
}

.mdc-menu .mdc-list-item__graphic,
.mdc-menu .mdc-list-item__meta {
    color: rgba(0, 0, 0, .87);
    color: var(--mdc-theme-text-primary-on-background, rgba(0, 0, 0, .87))
}

.mdc-menu .mdc-list-divider {
    margin: 8px 0
}

.mdc-menu .mdc-list-item {
    cursor: pointer;
    user-select: none
}

.mdc-menu .mdc-list-item--disabled {
    cursor: auto
}

.mdc-menu a.mdc-list-item .mdc-list-item__graphic,
.mdc-menu a.mdc-list-item .mdc-list-item__text {
    pointer-events: none
}

.mdc-menu__selection-group {
    padding: 0;
    fill: currentColor
}

.mdc-menu__selection-group .mdc-list-item {
    padding-left: 56px;
    padding-right: 0
}

.mdc-menu__selection-group .mdc-list-item[dir=rtl],
[dir=rtl] .mdc-menu__selection-group .mdc-list-item {
    padding-left: 0;
    padding-right: 56px
}

.mdc-menu__selection-group-icon {
    left: 16px;
    right: auto;
    display: none;
    position: absolute
}

.mdc-menu__selection-group-icon[dir=rtl],
[dir=rtl] .mdc-menu__selection-group-icon {
    left: auto;
    right: 16px
}

.mdc-menu-item--selected .mdc-menu__selection-group-icon {
    display: inline
}

