.mdc-tab-bar {
    width: 100%
}

.mdc-tab-scroller {
    overflow-y: hidden
}

.mdc-tab-scroller__test {
    position: absolute;
    top: -9999px;
    width: 100px;
    height: 100px;
    overflow-x: scroll
}

.mdc-tab-scroller__scroll-area {
    -webkit-overflow-scrolling: touch;
    display: flex;
    overflow-x: hidden
}

.mdc-tab-scroller__scroll-area::-webkit-scrollbar,
.mdc-tab-scroller__test::-webkit-scrollbar {
    display: none
}

.mdc-tab-scroller__scroll-area--scroll {
    overflow-x: scroll
}

.mdc-tab-scroller__scroll-content {
    position: relative;
    display: flex;
    flex: 1 0 auto;
    transform: none;
    will-change: transform
}

.mdc-tab-scroller--align-start .mdc-tab-scroller__scroll-content {
    justify-content: flex-start
}

.mdc-tab-scroller--align-end .mdc-tab-scroller__scroll-content {
    justify-content: flex-end
}

.mdc-tab-scroller--align-center .mdc-tab-scroller__scroll-content {
    justify-content: center
}

.mdc-tab-scroller--animating .mdc-tab-scroller__scroll-area {
    -webkit-overflow-scrolling: auto
}

.mdc-tab-scroller--animating .mdc-tab-scroller__scroll-content {
    transition: transform .25s cubic-bezier(.4, 0, .2, 1)
}

.mdc-tab-indicator {
    display: flex;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1
}

.mdc-tab-indicator .mdc-tab-indicator__content--underline {
    border-color: #9fbadb;
    border-color: var(--mdc-theme-primary, #9fbadb);
    border-top-width: 2px
}

.mdc-tab-indicator .mdc-tab-indicator__content--icon {
    color: #9fbadb;
    color: var(--mdc-theme-secondary, #9fbadb);
    height: 34px;
    font-size: 34px
}

.mdc-tab-indicator__content {
    transform-origin: left;
    opacity: 0
}

.mdc-tab-indicator__content--underline {
    align-self: flex-end;
    box-sizing: border-box;
    width: 100%;
    border-top-style: solid
}

.mdc-tab-indicator__content--icon {
    align-self: center;
    margin: 0 auto
}

.mdc-tab-indicator--active .mdc-tab-indicator__content {
    opacity: 1
}

.mdc-tab-indicator .mdc-tab-indicator__content {
    transition: transform .25s cubic-bezier(.4, 0, .2, 1)
}

.mdc-tab-indicator--no-transition .mdc-tab-indicator__content {
    transition: none
}

.mdc-tab-indicator--fade .mdc-tab-indicator__content {
    transition: opacity .15s linear
}

.mdc-tab-indicator--active.mdc-tab-indicator--fade .mdc-tab-indicator__content {
    transition-delay: .1s
}

.mdc-tab {
    position: relative;
    font-family: Roboto, sans-serif;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    font-size: .875rem;
    line-height: 2.25rem;
    font-weight: 500;
    letter-spacing: .08928571em;
    text-decoration: none;
    text-transform: uppercase;
    display: flex;
    flex: 1 0 auto;
    justify-content: center;
    box-sizing: border-box;
    height: 48px;
    margin: 0;
    padding: 0 24px;
    border: none;
    outline: none;
    background: none;
    text-align: center;
    white-space: nowrap;
    cursor: pointer;
    -webkit-appearance: none;
    z-index: 1
}

.mdc-tab .mdc-tab__text-label {
    color: rgba(0, 0, 0, .6)
}

.mdc-tab .mdc-tab__icon {
    color: rgba(0, 0, 0, .54);
    fill: currentColor
}

.mdc-tab::-moz-focus-inner {
    padding: 0;
    border: 0
}

.mdc-tab--min-width {
    flex: 0 1 auto
}

.mdc-tab__ripple {
    --mdc-ripple-fg-size: 0;
    --mdc-ripple-left: 0;
    --mdc-ripple-top: 0;
    --mdc-ripple-fg-scale: 1;
    --mdc-ripple-fg-translate-end: 0;
    --mdc-ripple-fg-translate-start: 0;
    -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden
}

.mdc-tab__ripple:after,
.mdc-tab__ripple:before {
    position: absolute;
    border-radius: 50%;
    opacity: 0;
    pointer-events: none;
    content: ""
}

.mdc-tab__ripple:before {
    transition: opacity 15ms linear, background-color 15ms linear;
    z-index: 1
}

.mdc-tab__ripple.mdc-ripple-upgraded:before {
    transform: scale(var(--mdc-ripple-fg-scale, 1))
}

.mdc-tab__ripple.mdc-ripple-upgraded:after {
    top: 0;
    left: 0;
    transform: scale(0);
    transform-origin: center center
}

.mdc-tab__ripple.mdc-ripple-upgraded--unbounded:after {
    top: var(--mdc-ripple-top, 0);
    left: var(--mdc-ripple-left, 0)
}

.mdc-tab__ripple.mdc-ripple-upgraded--foreground-activation:after {
    animation: mdc-ripple-fg-radius-in 225ms forwards, mdc-ripple-fg-opacity-in 75ms forwards
}

.mdc-tab__ripple.mdc-ripple-upgraded--foreground-deactivation:after {
    animation: mdc-ripple-fg-opacity-out .15s;
    transform: translate(var(--mdc-ripple-fg-translate-end, 0)) scale(var(--mdc-ripple-fg-scale, 1))
}

.mdc-tab__ripple:after,
.mdc-tab__ripple:before {
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%
}

.mdc-tab__ripple.mdc-ripple-upgraded:after {
    width: var(--mdc-ripple-fg-size, 100%);
    height: var(--mdc-ripple-fg-size, 100%)
}

.mdc-tab__ripple:after,
.mdc-tab__ripple:before {
    background-color: #9fbadb
}

@supports not (-ms-ime-align:auto) {
    .mdc-tab__ripple:after,
    .mdc-tab__ripple:before {
        background-color: var(--mdc-theme-primary, #9fbadb)
    }
}

.mdc-tab__ripple:hover:before {
    opacity: .08
}

.mdc-tab__ripple.mdc-ripple-upgraded--background-focused:before,
.mdc-tab__ripple:not(.mdc-ripple-upgraded):focus:before {
    transition-duration: 75ms;
    opacity: .24
}

.mdc-tab__ripple:not(.mdc-ripple-upgraded):after {
    transition: opacity .15s linear
}

.mdc-tab__ripple:not(.mdc-ripple-upgraded):active:after {
    transition-duration: 75ms;
    opacity: .24
}

.mdc-tab__ripple.mdc-ripple-upgraded {
    --mdc-ripple-fg-opacity: 0.24
}

