<header class="wc-header">
    <div class="wc-header__container">
        <div class="wc-header__left-menu">
            <div class="wc-header__left-menu-item header__continue_shopping" data-menu-item-id="185">
                <a href="http://wildcrafter.com/">
                    <i class="md-icons">arrow_back</i>
                    <span>CONTINUE SHOPPING </span>
                </a>
            </div>
        </div>
        <div class="wc-header__logo-wrapper">
            <a class="wc-header__logo-container" href="../">
                <img class="wc-header__logo-large" src="{{getImage settings.store_logo.image 'logo_size'}}"></img>
                <i class="wc-header__logo-sticky icon-wildcrafter-logo"></i>
            </a>
        </div>
    </div>
</header>

<div class="container">
    {{{ checkout.checkout_content }}}
    <div class="layout-cart is-sticky">
        <div class="checkout-order__container">
            <div class="checkout-order__titles">
                <h2 class="checkout-order__title">Your Order</h2>
                <p class="checkout-order__text">Your discount has been applied to your order</p>
            </div>
            <div class="order__container" data-cart-content>
                {{> components/cart/cart-content}}
            </div>
            <a href="../cart.php" class="mdc-button mdc-button--small primary"> EDIT CART </a>
        </div>
        <div class="order_coupon_code_form">
            {{> components/cart/coupon-input}}
        </div>
        <div class="order-cart-totals-view" data-cart-totals>
            {{> components/cart/totals}}
        </div>
    </div>

<footer class="wc-footer-custom">
    <div class="wc-footer__menu-custom">
        <a class="wc-footer__menu-custom-link" href="https://support.wildcrafter.com/hc/en-us/categories/360002006492-Returns">Refund Policy</a>
    </div>
