<script>
    window.addEventListener('resize', function(){ 
        if(window.outerWidth <= 968){
            document.getElementById('checkout-app').style.width='100%';
        } else { 
            document.getElementById('checkout-app').style.width=null; 
        }
    });

    var html_loaded = false;
    jQuery(window).load(function() {
        var totalDiscount = 0;
        jQuery(".price--discounted").each(function() {
            var getTotal = jQuery(this).attr('total');
            var getDiscount = jQuery(this).attr('discount');
            var calc = parseFloat(getTotal - getDiscount);
            totalDiscount = totalDiscount + calc;
        });
            if(totalDiscount === 0)
                jQuery(".checkout-totals-discounts").text('$'+totalDiscount.toFixed(2));
            else
                jQuery(".checkout-totals-discounts").text('-$'+totalDiscount.toFixed(2));
        order_loaded()
        jQuery("body").on("DOMSubtreeModified", function() {
            order_loaded()
        })

        jQuery(".coupons-remove-buttons").click(function() {
            var action_url = jQuery(this).attr('url')
            jQuery.post(action_url, function(){
                location.reload();
            })
        })
        
        
    })

    function order_loaded() {
        // jQuery(".form-body button").addClass('mdc-button mdc-button--x-small primary');
        // if(jQuery(".instrumentSelect").hasClass("instrumentSelect-button")){
        //     jQuery(".instrumentSelect-button").removeClass('mdc-button mdc-button--x-small primary');
        // }
        
    
        jQuery(".customerView-actions .optimizedCheckout-buttonSecondary").addClass('mdc-button mdc-button--x-small primary');
        jQuery(".stepHeader-actions .optimizedCheckout-buttonSecondary").addClass('mdc-button mdc-button--small text');
        jQuery(".instrumentModal-trigger").addClass('mdc-button mdc-button--x-small primary');
        jQuery("#checkout-customer-continue").addClass('mdc-button mdc-button--x-small primary');
        jQuery("#checkout-customer-cancel").addClass('mdc-button mdc-button--x-small primary');
        jQuery("#checkout-payment-continue").addClass('mdc-button mdc-button--x-small primary');
        jQuery("#checkout-shipping-continue").addClass('mdc-button mdc-button--x-small primary');
        jQuery("#checkout-customer-login").addClass('mdc-button mdc-button--x-small primary');
        jQuery("#applyRedeemableButton").addClass('mdc-button mdc-button--x-small secondary');
        jQuery("#checkout-billing-continue").addClass('mdc-button mdc-button--x-small primary');

        jQuery(".layout-main input[type=email]").each(function() {
            var id = jQuery(this).attr('id');
            var lable = jQuery("label[for=" + id + "].form-label").text();
            jQuery(this).attr('placeholder', lable);
        })
        jQuery(".layout-main input[type=password]").each(function() {
            var id = jQuery(this).attr('id');
            var lable = jQuery("label[for=" + id + "].form-label").text();
            jQuery(this).attr('placeholder', lable);
        })
        jQuery(".layout-main input[type=text]").each(function() {
            var id = jQuery(this).attr('id');
            var lable = jQuery("label[for=" + id + "].form-label").text();
            jQuery(this).attr('placeholder', lable);

        })
        jQuery(".layout-main input[type=tel]").each(function() {
            var id = jQuery(this).attr('id');
            var lable = jQuery("label[for=" + id + "].form-label").text();
            jQuery(this).attr('placeholder', lable);
        })
        jQuery(".checkout-form a").attr('target', '_self');
        jQuery("input[name=redeemableCode]").attr('placeholder', "Promo Code");

        



        setTimeout(function() {
            if (!jQuery("#checkout-customer-returning span").hasClass('continue-guest-account')) {
                jQuery("#checkout-customer-returning span").html('Donâ€™t have an account? <span class="continue-guest-account" onclick="continueGuest()">Continue as guest</span> to continue.');
                jQuery(".ng-checkout-container").addClass("show-actions");
                jQuery("#checkout-customer-returning").addClass("show-account");
                jQuery("#checkout-customer-cancel").parent().append("<a class='button optimizedCheckout-buttonSecondary mdc-button mdc-button--x-small secondary' href='../login.php?action=create_account' target='_self'>sign up</a>");
            }
            if(jQuery('.layout-cart span').hasClass('cart-priceItem-postFix')){
                if(!jQuery('.cart-totals li').hasClass('coupons-data-show-checkout')){
                    location.reload();
                }
            }
            console.log("----");
            if (!html_loaded) {
                jQuery(".stepHeader-title.optimizedCheckout-headingPrimary")[0].textContent = "Account";
            }
            if (!html_loaded && jQuery(".ng-checkout-container").hasClass("show-actions")) {

                jQuery(".redeemable-label")[0].click();
                html_loaded = true;
                //setTimeout(function() {
                    if (!jQuery(".order_coupon_code_form").hasClass("show-coupon-code")) {
                        jQuery(".order_coupon_code_form").addClass("show-coupon-code");
                        jQuery(".layout-cart div[data-test=redeemable-collapsable]").appendTo(jQuery(".order_coupon_code_form"));
                    }
                //}, 500)
                if(jQuery("#checkout-customer-login")[0])
                    jQuery("#checkout-customer-login")[0].click();                
            }
        }, 1000);
    }

    function continueGuest() {
        jQuery("#checkout-customer-cancel")[0].click();
    }
</script>
